import React, { useState } from "react";
import { View, Text, TouchableOpacity, ScrollView } from 'react-native';
import { SafeAreaView } from "react-native";
import FloatingLabel from "../../components/FloatingLabel";
import Header from "../../components/Header";
import { color } from "../../utils/color";
import { GlobalStyles } from "../../utils/globalStyles";

export default function AddAddress({ navigation }) {

    const [name, setName] = useState('')
    const [phone, setPhone] = useState('')
    const [pincode, setPincode] = useState('')
    const [state, setState] = useState('')
    const [address, setAddress] = useState('')
    const [city, setCity] = useState('')
    const [locality, setLocality] = useState('')

    return (
        <SafeAreaView style={{ backgroundColor: color.primary }}>
            <Header type="back" title="Add new address" />
            <View style={{ height: "100%", width: "100%", paddingHorizontal: 15, paddingVertical: 20, backgroundColor: color.white }}>
                <ScrollView>
                    <FloatingLabel
                        value={name}
                        style={{ backgroundColor: color.transparent, marginTop: 20, borderColor: "#c4c4c4", borderRadius: 5, color: 'rgba(0, 0, 0, 0.6)' }}
                        label={"Name"}
                        labelColor={color.black}
                        inputStyle={{ color: 'rgba(0, 0, 0, 0.6)', fontSize: 12 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setName(value);
                        }}
                    />
                    <FloatingLabel
                        value={phone}
                        style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: 'rgba(0, 0, 0, 0.6)' }}
                        label={"Mobile Number"}
                        labelColor={color.black}
                        inputStyle={{ color: 'rgba(0, 0, 0, 0.6)', fontSize: 12 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setPhone(value);
                        }}
                    />
                    <View style={{ flexDirection: "row", alignItems: "center", justifyContent: "space-between" }}>
                        <FloatingLabel
                            value={pincode}
                            style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: 'rgba(0, 0, 0, 0.6)', width: '47%' }}
                            label={"Pincode"}
                            labelColor={color.black}
                            inputStyle={{ color: 'rgba(0, 0, 0, 0.6)', fontSize: 12 }}
                            autoCapitalize="none"
                            onChangeText={(value) => {
                                setPincode(value);
                            }}
                        />
                        <FloatingLabel
                            value={state}
                            style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: 'rgba(0, 0, 0, 0.6)', width: '47%' }}
                            label={"State"}
                            labelColor={color.black}
                            inputStyle={{ color: 'rgba(0, 0, 0, 0.6)', fontSize: 12 }}
                            autoCapitalize="none"
                            onChangeText={(value) => {
                                setState(value);
                            }}
                        />
                    </View>
                    <FloatingLabel
                        value={address}
                        style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: 'rgba(0, 0, 0, 0.6)' }}
                        label={"Address"}
                        labelColor={color.black}
                        inputStyle={{ color: 'rgba(0, 0, 0, 0.6)', fontSize: 12 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setAddress(value);
                        }}
                    />
                    <FloatingLabel
                        value={city}
                        style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: 'rgba(0, 0, 0, 0.6)' }}
                        label={"City"}
                        labelColor={color.black}
                        inputStyle={{ color: 'rgba(0, 0, 0, 0.6)', fontSize: 12 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setCity(value);
                        }}
                    />
                    <FloatingLabel
                        value={locality}
                        style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: 'rgba(0, 0, 0, 0.6)' }}
                        label={"Locality"}
                        labelColor={color.black}
                        inputStyle={{ color: 'rgba(0, 0, 0, 0.6)', fontSize: 12 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setLocality(value);
                        }}
                    />
                    <TouchableOpacity onPress={() => navigation.navigate("Payment")} style={{ justifyContent: 'center', alignItems: 'center', height: 45, backgroundColor: color.primary, borderRadius: 5, marginTop: 20 }}>
                        <Text style={{ fontSize: 16, color: color.white, ...GlobalStyles.dm_sans_bold }}>Place Order</Text>
                    </TouchableOpacity>
                </ScrollView>
            </View>
        </SafeAreaView>
    )
}
