import React, { useState } from "react";
import { View, Text, TouchableOpacity, ScrollView } from 'react-native';
import { SafeAreaView } from "react-native";
import { useSelector } from "react-redux";
import FloatingLabel from "../../components/FloatingLabel";
import Header from "../../components/Header";
import common_axios from "../../utils/axios";
import { color } from "../../utils/color";
import { GlobalStyles } from "../../utils/globalStyles";

export default function AddAddress({ navigation }) {

    const [name, setName] = useState('')
    const [phone, setPhone] = useState('')
    const [pincode, setPincode] = useState('')
    const [state, setState] = useState('')
    const [address, setAddress] = useState('')
    const [city, setCity] = useState('')
    const [locality, setLocality] = useState('')

    const { user_data } = useSelector(state => state.reducer)

    const submit = async () => {
        if (name.length == 0) {
            alert("Enter a valid name");
            return;
        }

        if (phone.length != 10) {
            alert("Enter a valid mobile number");
            return;
        }

        if (pincode.length != 6) {
            alert("Enter a valid pincode");
            return;
        }
        if (state.length == 0) {
            alert("Enter a valid state");
            return;
        }
        if (city.length == 0) {
            alert("Enter a valid city");
            return;
        }
        if (locality.length == 0) {
            alert("Enter a valid locality");
            return;
        }

        try {
            const { data } = await common_axios.post('/useraddress', {
                user_id: user_data.id,
                pincode,
                state,
                address,
                city,
                locality,
                phone,
                name
            });
            if (data.status == "1") {
                navigation.goBack(null)
            }
        } catch (e) {
            console.log(e)
        }
    }

    return (
        <SafeAreaView style={{ backgroundColor: color.primary }}>
            <Header type="back" title="Add new address" />
            <View style={{ height: "100%", width: "100%", paddingHorizontal: 15, paddingVertical: 20, backgroundColor: color.white }}>
                <ScrollView>
                    <FloatingLabel
                        value={name}
                        style={{ backgroundColor: color.transparent, marginTop: 20, borderColor: "#c4c4c4", borderRadius: 5, color: color.black }}
                        label={"Name"}
                        labelColor={color.black}
                        inputStyle={{ color: color.black, fontSize: 16 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setName(value);
                        }}
                    />
                    <FloatingLabel
                        value={phone}
                        style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: color.black }}
                        label={"Mobile Number"}
                        labelColor={color.black}
                        inputStyle={{ color: color.black, fontSize: 16 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setPhone(value);
                        }}
                    />
                    <View style={{ flexDirection: "row", alignItems: "center", justifyContent: "space-between" }}>
                        <FloatingLabel
                            value={pincode}
                            style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: color.black, width: '47%' }}
                            label={"Pincode"}
                            labelColor={color.black}
                            inputStyle={{ color: color.black, fontSize: 16 }}
                            autoCapitalize="none"
                            onChangeText={(value) => {
                                setPincode(value);
                            }}
                        />
                        <FloatingLabel
                            value={state}
                            style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: color.black, width: '47%' }}
                            label={"State"}
                            labelColor={color.black}
                            inputStyle={{ color: color.black, fontSize: 16 }}
                            autoCapitalize="none"
                            onChangeText={(value) => {
                                setState(value);
                            }}
                        />
                    </View>
                    <FloatingLabel
                        value={address}
                        style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: color.black }}
                        label={"Address"}
                        labelColor={color.black}
                        inputStyle={{ color: color.black, fontSize: 16 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setAddress(value);
                        }}
                    />
                    <FloatingLabel
                        value={city}
                        style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: color.black }}
                        label={"City"}
                        labelColor={color.black}
                        inputStyle={{ color: color.black, fontSize: 16 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setCity(value);
                        }}
                    />
                    <FloatingLabel
                        value={locality}
                        style={{ backgroundColor: color.transparent, marginTop: 40, borderColor: "#c4c4c4", borderRadius: 5, color: color.black }}
                        label={"Locality"}
                        labelColor={color.black}
                        inputStyle={{ color: color.black, fontSize: 16 }}
                        autoCapitalize="none"
                        onChangeText={(value) => {
                            setLocality(value);
                        }}
                    />
                    <TouchableOpacity onPress={() => submit()} style={{ justifyContent: 'center', alignItems: 'center', height: 45, backgroundColor: color.primary, borderRadius: 5, marginTop: 30 }}>
                        <Text style={{ fontSize: 16, color: color.white, ...GlobalStyles.dm_sans_bold }}>Submit</Text>
                    </TouchableOpacity>
                </ScrollView>
            </View>
        </SafeAreaView>
    )
}
