import React, { useState } from 'react';
import Container from '../../components/Container';
import { View, Text } from 'react-native'
import { color } from '../../utils/color';
import Button from '../../components/Button';
import { GlobalStyles } from '../../utils/globalStyles';
import FloatingLabel from '../../components/FloatingLabel';
import common_axios from '../../utils/axios';
import { useDispatch } from 'react-redux';
import { setIsUser, setUserData } from '../../redux/actions/main';
import AsyncStorage from '@react-native-async-storage/async-storage';

function Register({ navigation }) {

    const [email, setEmail] = useState("");
    const [username, setUsername] = useState("")
    const [phone, setPhone] = useState("");
    const [password, setPassword] = useState("")
    const dispatch = useDispatch();

    const handlePress = async () => {
        let reg = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

        if (username.length == 0) {
            alert("Enter a valid username");
            return;
        }

        if (phone.length != 10) {
            alert("Enter a valid phone num")
            return;
        }

        if (!reg.test(email)) {
            alert("Enter a valid email");
            return;
        }

        if (password.length < 5) {
            alert("Enter a valid password")
            return;
        }

        try {
            const { data } = await common_axios.post("/auth/register", {
                name: username,
                email,
                password,
                phone,
                country: "India"
            })
            console.log(data)
            if (data.data) {
                dispatch(setUserData(data.data))
                dispatch(setIsUser(true))
                await AsyncStorage.setItem('access_token', JSON.stringify(data.data?.api_token))
                await AsyncStorage.setItem('user_data', JSON.stringify(data.data))
            }
        } catch (e) {
            console.log(e)
            alert("Server error")
        }
    }

    return (
        <Container style={{ justifyContent: 'center', alignItems: 'center' }}>
            <Text style={{ fontSize: 28, color: color.white, ...GlobalStyles.bold_text }}>Welcome!</Text>
            <Text style={{ fontSize: 16, color: color.white, marginTop: 10, ...GlobalStyles.semi_bold_text }}>Create your account to get started</Text>
            <FloatingLabel
                value={username}
                style={{ backgroundColor: color.transparent, marginTop: 50 }}
                label={"User Name"}
                autoCapitalize="none"
                onChangeText={(value) => {
                    setUsername(value);
                }}
            />
            <FloatingLabel
                value={phone}
                style={{ backgroundColor: color.transparent, marginTop: 40 }}
                label={"Phone Number"}
                maxLength={10}
                keyboardType="phone-pad"
                autoCapitalize="none"
                onChangeText={(value) => {
                    setPhone(value);
                }}
            />
            <FloatingLabel
                value={email}
                style={{ backgroundColor: color.transparent, marginTop: 40 }}
                label={"Email Address"}
                autoCapitalize="none"
                onChangeText={(value) => {
                    setEmail(value);
                }}
            />
            {/* <FloatingLabel
                value={password}
                secureTextEntry={true}
                style={{ backgroundColor: color.transparent, marginTop: 40 }}
                label={"Password"}
                autoCapitalize="none"
                onChangeText={(value) => {
                    setPassword(value);
                }}
            /> */}
            <Button titleStyle={{ fontSize: 16 }} style={{ marginTop: 25 }} title={"Register"} onPress={() => handlePress()} />
            <Text style={{ fontSize: 14, color: color.light_grey, marginTop: 10, ...GlobalStyles.regular_text }}>Already have an account? <Text onPress={() => navigation.navigate("Login")} style={{ color: color.white }}>Login</Text></Text>
        </Container>
    )
}

export default Register;
